import React, { useState } from 'react';
import './Registration.css';

const Registration = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        mobileNumber: '',
        password: '',
    });

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Handle input change
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
        setError(''); // Clear error on input change
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validation check for empty fields
        if (!formData.firstName || !formData.lastName || !formData.mobileNumber || !formData.password) {
            setError('All fields are required');
            return;
        }

        // Validation for mobile number
        if (!/^\d{10}$/.test(formData.mobileNumber)) {
            setError('Mobile number must be 10 digits');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('User registered:', data);
                setSuccess('Registration successful!');
                setError('');
                setFormData({
                    firstName: '',
                    lastName: '',
                    mobileNumber: '',
                    password: '',
                });

                // Redirect to login page after successful registration
                setTimeout(() => {
                    window.location.href = '/login';
                }, 2000); // 2 seconds delay for user feedback
            } else {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to register. Please try again later.');
            }
        } catch (error) {
            setError('An error occurred. Please try again.');
            console.error('Error:', error);
        }
    };

    return (
        <div id="root" className="registration-container">
            <h2>Registration</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    placeholder="First Name"
                    required
                />
                <input
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    placeholder="Last Name"
                    required
                />
                <input
                    type="tel"
                    name="mobileNumber"
                    value={formData.mobileNumber}
                    onChange={handleChange}
                    placeholder="Mobile Number"
                    pattern="[0-9]{10}"
                    required
                />
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Password"
                    required
                />
                <div className="social-login">
                    <button type="button">Login with Google</button>
                    <button type="button">Login with Facebook</button>
                    <button type="button">Login with Apple ID</button>
                </div>
                <button type="submit">Register</button>
            </form>
            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}
        </div>
    );
};

export default Registration;
